declare namespace camelcase {
	interface Options {
		/**
		Uppercase the first character: `bar-bar` → `FooBar`.

		@default false
		*/
		readonly pascalCase?: boolean;
	}
}

declare const camelcase: {
	/**
	Convert a dash/dot/underscore/space separated string to camelCase or PascalCase: `bar-bar` → `barBar`.

	@param input - String to convert to camel case.

	@example
	```
	import camelCase = require('camelcase');

	camelCase('bar-bar');
	//=> 'barBar'

	camelCase('bar_bar');
	//=> 'barBar'

	camelCase('Foo-Bar');
	//=> 'barBar'

	camelCase('Foo-Bar', {pascalCase: true});
	//=> 'FooBar'

	camelCase('--bar.bar', {pascalCase: false});
	//=> 'barBar'

	camelCase('bar bar');
	//=> 'barBar'

	console.log(process.argv[3]);
	//=> '--bar-bar'
	camelCase(process.argv[3]);
	//=> 'barBar'

	camelCase(['bar', 'bar']);
	//=> 'barBar'

	camelCase(['__bar__', '--bar'], {pascalCase: true});
	//=> 'FooBar'
	```
	*/
	(input: string | ReadonlyArray<string>, options?: camelcase.Options): string;

	// TODO: Remove this for the next major release, refactor the whole definition to:
	// declare function camelcase(
	// 	input: string | ReadonlyArray<string>,
	// 	options?: camelcase.Options
	// ): string;
	// export = camelcase;
	default: typeof camelcase;
};

export = camelcase;
