'use strict'

const t = require('tap')
const test = t.test
const FindMyWay = require('../')

test('Falling back for node\'s parametric brother', t => {
  t.plan(3)
  const findMyWay = FindMyWay({
    defaultRoute: (req, res) => {
      t.fail('Should not be defaultRoute')
    }
  })

  findMyWay.on('GET', '/bar/:id', () => {})
  findMyWay.on('GET', '/bar/:color/:id', () => {})
  findMyWay.on('GET', '/bar/red', () => {})

  t.same(findMyWay.find('GET', '/bar/red/123').params, { color: 'red', id: '123' })
  t.same(findMyWay.find('GET', '/bar/blue/123').params, { color: 'blue', id: '123' })
  t.same(findMyWay.find('GET', '/bar/red').params, {})
})
