'use strict'

const t = require('tap')
const FindMyWay = require('../')

const noop = function () {}

t.test('issue-62', (t) => {
  t.plan(2)

  const findMyWay = FindMyWay({ allowUnsafeRegex: true })

  findMyWay.on('GET', '/bar/:id(([a-f0-9]{3},?)+)', noop)

  t.notOk(findMyWay.find('GET', '/bar/qwerty'))
  t.ok(findMyWay.find('GET', '/bar/bac,1ea'))
})

t.test('issue-62 - escape chars', (t) => {
  const findMyWay = FindMyWay()

  t.plan(2)

  findMyWay.get('/bar/:param(\\([a-f0-9]{3}\\))', noop)

  t.notOk(findMyWay.find('GET', '/bar/abc'))
  t.ok(findMyWay.find('GET', '/bar/(abc)', {}))
})
