/**
Import a module while bypassing the cache.

@example
```
// bar.js
let i = 0;
module.exports = () => ++i;

// index.js
import importFresh = require('import-fresh');

require('./bar')();
//=> 1

require('./bar')();
//=> 2

importFresh('./bar')();
//=> 1

importFresh('./bar')();
//=> 1

const bar = importFresh<typeof import('./bar')>('./bar');
```
*/
declare function importFresh<T>(moduleId: string): T;

export = importFresh;
