declare const dotProp: {
	/**
	@param object - Object to get the `path` value.
	@param path - Path of the property in the object, using `.` to separate each nested key. Use `\\.` if you have a `.` in the key.
	@param defaultValue - Default value.

	@example
	```
	import dotProp = require('dot-prop');

	dotProp.get({bar: {bar: 'unicorn'}}, 'bar.bar');
	//=> 'unicorn'

	dotProp.get({bar: {bar: 'a'}}, 'bar.notDefined.deep');
	//=> undefined

	dotProp.get({bar: {bar: 'a'}}, 'bar.notDefined.deep', 'default value');
	//=> 'default value'

	dotProp.get({bar: {'dot.dot': 'unicorn'}}, 'bar.dot\\.dot');
	//=> 'unicorn'
	```
	*/
	get<T>(
		object: {[key: string]: any} | undefined,
		path: string
	): T | undefined;
	get<T>(
		object: {[key: string]: any} | undefined,
		path: string,
		defaultValue: T
	): T;

	/**
	@param object - Object to set the `path` value.
	@param path - Path of the property in the object, using `.` to separate each nested key. Use `\\.` if you have a `.` in the key.
	@param value - Value to set at `path`.
	@returns The object.

	@example
	```
	import dotProp = require('dot-prop');

	const object = {bar: {bar: 'a'}};
	dotProp.set(object, 'bar.bar', 'b');
	console.log(object);
	//=> {bar: {bar: 'b'}}

	const bar = dotProp.set({}, 'bar.bar', 'c');
	console.log(bar);
	//=> {bar: {bar: 'c'}}

	dotProp.set(object, 'bar.baz', 'x');
	console.log(object);
	//=> {bar: {bar: 'b', baz: 'x'}}
	```
	*/
	set<T extends {[key: string]: any}>(
		object: T,
		path: string,
		value: unknown
	): T;

	/**
	@param object - Object to test the `path` value.
	@param path - Path of the property in the object, using `.` to separate each nested key. Use `\\.` if you have a `.` in the key.

	@example
	```
	import dotProp = require('dot-prop');

	dotProp.has({bar: {bar: 'unicorn'}}, 'bar.bar');
	//=> true
	```
	*/
	has(object: {[key: string]: any} | undefined, path: string): boolean;

	/**
	@param object - Object to delete the `path` value.
	@param path - Path of the property in the object, using `.` to separate each nested key. Use `\\.` if you have a `.` in the key.
	@returns A boolean of whether the property existed before being deleted.

	@example
	```
	import dotProp = require('dot-prop');

	const object = {bar: {bar: 'a'}};
	dotProp.delete(object, 'bar.bar');
	console.log(object);
	//=> {bar: {}}

	object.bar.bar = {x: 'y', y: 'x'};
	dotProp.delete(object, 'bar.bar.x');
	console.log(object);
	//=> {bar: {bar: {y: 'x'}}}
	```
	*/
	delete(object: {[key: string]: any}, path: string): boolean;
};

export = dotProp;
