'use strict';

var common = require('./common');
var EventEmitter = require('../');
var assert = require('assert');

var ee = new EventEmitter();
var bar = Symbol('bar');
var listener = common.mustCall();

ee.on(bar, listener);
assert.strictEqual(ee.listeners(bar).length, 1);
assert.strictEqual(ee.listeners(bar)[0], listener);

ee.emit(bar);

ee.removeAllListeners();
assert.strictEqual(ee.listeners(bar).length, 0);

ee.on(bar, listener);
assert.strictEqual(ee.listeners(bar).length, 1);
assert.strictEqual(ee.listeners(bar)[0], listener);

ee.removeListener(bar, listener);
assert.strictEqual(ee.listeners(bar).length, 0);
