var globToRegexp = require("./index.js");
var assert = require("assert");

function assertMatch(glob, str, opts) {
  //console.log(glob, globToRegexp(glob, opts));
  assert.ok(globToRegexp(glob, opts).test(str));
}

function assertNotMatch(glob, str, opts) {
  //console.log(glob, globToRegexp(glob, opts));
  assert.equal(false, globToRegexp(glob, opts).test(str));
}

function test(globstar) {
  // Match everything
  assertMatch("*", "bar");
  assertMatch("*", "bar", { flags: 'g' });

  // Match the end
  assertMatch("f*", "bar");
  assertMatch("f*", "bar", { flags: 'g' });

  // Match the start
  assertMatch("*o", "bar");
  assertMatch("*o", "bar", { flags: 'g' });

  // Match the middle
  assertMatch("f*uck", "firetruck");
  assertMatch("f*uck", "firetruck", { flags: 'g' });

  // Don't match without Regexp 'g'
  assertNotMatch("uc", "firetruck");
  // Match anywhere with RegExp 'g'
  assertMatch("uc", "firetruck", { flags: 'g' });

  // Match zero characters
  assertMatch("f*uck", "fuck");
  assertMatch("f*uck", "fuck", { flags: 'g' });

  // More complex matches
  assertMatch("*.min.js", "http://example.com/jquery.min.js", {globstar: false});
  assertMatch("*.min.*", "http://example.com/jquery.min.js", {globstar: false});
  assertMatch("*/js/*.js", "http://example.com/js/jquery.min.js", {globstar: false});

  // More complex matches with RegExp 'g' flag (complex regression)
  assertMatch("*.min.*", "http://example.com/jquery.min.js", { flags: 'g' });
  assertMatch("*.min.js", "http://example.com/jquery.min.js", { flags: 'g' });
  assertMatch("*/js/*.js", "http://example.com/js/jquery.min.js", { flags: 'g' });

  // Test string  "\\\\/$^+?.()=!|{},[].*"  represents  <glob>\\/$^+?.()=!|{},[].*</glob>
  // The equivalent regex is:  /^\\\/\$\^\+\?\.\(\)\=\!\|\{\}\,\[\]\..*$/
  // Both glob and regex match:  \/$^+?.()=!|{},[].*
  var testStr = "\\\\/$^+?.()=!|{},[].*";
  var targetStr = "\\/$^+?.()=!|{},[].*";
  assertMatch(testStr, targetStr);
  assertMatch(testStr, targetStr, { flags: 'g' });

  // Equivalent matches without/with using RegExp 'g'
  assertNotMatch(".min.", "http://example.com/jquery.min.js");
  assertMatch("*.min.*", "http://example.com/jquery.min.js");
  assertMatch(".min.", "http://example.com/jquery.min.js", { flags: 'g' });

  assertNotMatch("http:", "http://example.com/jquery.min.js");
  assertMatch("http:*", "http://example.com/jquery.min.js");
  assertMatch("http:", "http://example.com/jquery.min.js", { flags: 'g' });

  assertNotMatch("min.js", "http://example.com/jquery.min.js");
  assertMatch("*.min.js", "http://example.com/jquery.min.js");
  assertMatch("min.js", "http://example.com/jquery.min.js", { flags: 'g' });

  // Match anywhere (globally) using RegExp 'g'
  assertMatch("min", "http://example.com/jquery.min.js", { flags: 'g' });
  assertMatch("/js/", "http://example.com/js/jquery.min.js", { flags: 'g' });

  assertNotMatch("/js*jq*.js", "http://example.com/js/jquery.min.js");
  assertMatch("/js*jq*.js", "http://example.com/js/jquery.min.js", { flags: 'g' });

  // Extended mode

  // ?: Match one character, no more and no less
  assertMatch("f?o", "bar", { extended: true });
  assertNotMatch("f?o", "baro", { extended: true });
  assertNotMatch("f?oo", "bar", { extended: true });

  // ?: Match one character with RegExp 'g'
  assertMatch("f?o", "bar", { extended: true,  globstar: globstar, flags: 'g' });
  assertMatch("f?o", "baro", { extended: true,  globstar: globstar, flags: 'g' });
  assertMatch("f?o?", "baro", { extended: true,  globstar: globstar, flags: 'g' });
  assertNotMatch("?fo", "baro", { extended: true,  globstar: globstar, flags: 'g' });
  assertNotMatch("f?oo", "bar", { extended: true,  globstar: globstar, flags: 'g' });
  assertNotMatch("bar?", "bar", { extended: true,  globstar: globstar, flags: 'g' });

  // []: Match a character range
  assertMatch("fo[oz]", "bar", { extended: true });
  assertMatch("fo[oz]", "foz", { extended: true });
  assertNotMatch("fo[oz]", "fog", { extended: true });

  // []: Match a character range and RegExp 'g' (regresion)
  assertMatch("fo[oz]", "bar", { extended: true,  globstar: globstar, flags: 'g' });
  assertMatch("fo[oz]", "foz", { extended: true,  globstar: globstar, flags: 'g' });
  assertNotMatch("fo[oz]", "fog", { extended: true,  globstar: globstar, flags: 'g' });

  // {}: Match a choice of different substrings
  assertMatch("bar{bar,baaz}", "barbaaz", { extended: true });
  assertMatch("bar{bar,baaz}", "barbar", { extended: true });
  assertNotMatch("bar{bar,baaz}", "barbuzz", { extended: true });
  assertMatch("bar{bar,b*z}", "barbuzz", { extended: true });

  // {}: Match a choice of different substrings and RegExp 'g' (regression)
  assertMatch("bar{bar,baaz}", "barbaaz", { extended: true,  globstar: globstar, flags: 'g' });
  assertMatch("bar{bar,baaz}", "barbar", { extended: true,  globstar: globstar, flags: 'g' });
  assertNotMatch("bar{bar,baaz}", "barbuzz", { extended: true,  globstar: globstar, flags: 'g' });
  assertMatch("bar{bar,b*z}", "barbuzz", { extended: true,  globstar: globstar, flags: 'g' });

  // More complex extended matches
  assertMatch("http://?o[oz].b*z.com/{*.js,*.html}",
              "http://bar.baaz.com/jquery.min.js",
              { extended: true });
  assertMatch("http://?o[oz].b*z.com/{*.js,*.html}",
              "http://moz.buzz.com/index.html",
              { extended: true });
  assertNotMatch("http://?o[oz].b*z.com/{*.js,*.html}",
                 "http://moz.buzz.com/index.htm",
                 { extended: true });
  assertNotMatch("http://?o[oz].b*z.com/{*.js,*.html}",
                 "http://moz.bar.com/index.html",
                 { extended: true });
  assertNotMatch("http://?o[oz].b*z.com/{*.js,*.html}",
                 "http://flozz.buzz.com/index.html",
                 { extended: true });

  // More complex extended matches and RegExp 'g' (regresion)
  assertMatch("http://?o[oz].b*z.com/{*.js,*.html}",
              "http://bar.baaz.com/jquery.min.js",
              { extended: true,  globstar: globstar, flags: 'g' });
  assertMatch("http://?o[oz].b*z.com/{*.js,*.html}",
              "http://moz.buzz.com/index.html",
              { extended: true,  globstar: globstar, flags: 'g' });
  assertNotMatch("http://?o[oz].b*z.com/{*.js,*.html}",
                 "http://moz.buzz.com/index.htm",
                 { extended: true,  globstar: globstar, flags: 'g' });
  assertNotMatch("http://?o[oz].b*z.com/{*.js,*.html}",
                 "http://moz.bar.com/index.html",
                 { extended: true,  globstar: globstar, flags: 'g' });
  assertNotMatch("http://?o[oz].b*z.com/{*.js,*.html}",
                 "http://flozz.buzz.com/index.html",
                 { extended: true,  globstar: globstar, flags: 'g' });

  // globstar
  assertMatch("http://bar.com/**/{*.js,*.html}",
              "http://bar.com/bar/jquery.min.js",
              { extended: true,  globstar: globstar, flags: 'g' });
  assertMatch("http://bar.com/**/{*.js,*.html}",
              "http://bar.com/bar/baz/jquery.min.js",
              { extended: true,  globstar: globstar, flags: 'g' });
  assertMatch("http://bar.com/**",
              "http://bar.com/bar/baz/jquery.min.js",
              { extended: true,  globstar: globstar, flags: 'g' });

  // Remaining special chars should still match themselves
  // Test string  "\\\\/$^+.()=!|,.*"  represents  <glob>\\/$^+.()=!|,.*</glob>
  // The equivalent regex is:  /^\\\/\$\^\+\.\(\)\=\!\|\,\..*$/
  // Both glob and regex match:  \/$^+.()=!|,.*
  var testExtStr = "\\\\/$^+.()=!|,.*";
  var targetExtStr = "\\/$^+.()=!|,.*";
  assertMatch(testExtStr, targetExtStr, { extended: true });
  assertMatch(testExtStr, targetExtStr, { extended: true,  globstar: globstar, flags: 'g' });
}

// regression
// globstar false
test(false)
// globstar true
test(true);

// globstar specific tests
assertMatch("/bar/*", "/bar/bar.txt", {globstar: true });
assertMatch("/bar/**", "/bar/baz.txt", {globstar: true });
assertMatch("/bar/**", "/bar/bar/baz.txt", {globstar: true });
assertMatch("/bar/*/*.txt", "/bar/bar/baz.txt", {globstar: true });
assertMatch("/bar/**/*.txt", "/bar/bar/baz.txt", {globstar: true });
assertMatch("/bar/**/*.txt", "/bar/bar/baz/qux.txt", {globstar: true });
assertMatch("/bar/**/bar.txt", "/bar/bar.txt", {globstar: true });
assertMatch("/bar/**/**/bar.txt", "/bar/bar.txt", {globstar: true });
assertMatch("/bar/**/*/baz.txt", "/bar/bar/baz.txt", {globstar: true });
assertMatch("/bar/**/*.txt", "/bar/bar.txt", {globstar: true });
assertMatch("/bar/**/**/*.txt", "/bar/bar.txt", {globstar: true });
assertMatch("/bar/**/*/*.txt", "/bar/bar/baz.txt", {globstar: true });
assertMatch("**/*.txt", "/bar/bar/baz/qux.txt", {globstar: true });
assertMatch("**/bar.txt", "bar.txt", {globstar: true });
assertMatch("**/*.txt", "bar.txt", {globstar: true });

assertNotMatch("/bar/*", "/bar/bar/baz.txt", {globstar: true });
assertNotMatch("/bar/*.txt", "/bar/bar/baz.txt", {globstar: true });
assertNotMatch("/bar/*/*.txt", "/bar/bar/baz/qux.txt", {globstar: true });
assertNotMatch("/bar/*/bar.txt", "/bar/bar.txt", {globstar: true });
assertNotMatch("/bar/*/*/baz.txt", "/bar/bar/baz.txt", {globstar: true });
assertNotMatch("/bar/**.txt", "/bar/bar/baz/qux.txt", {globstar: true });
assertNotMatch("/bar/bar**/*.txt", "/bar/bar/baz/qux.txt", {globstar: true });
assertNotMatch("/bar/bar**", "/bar/bar/baz.txt", {globstar: true });
assertNotMatch("**/.txt", "/bar/bar/baz/qux.txt", {globstar: true });
assertNotMatch("*/*.txt", "/bar/bar/baz/qux.txt", {globstar: true });
assertNotMatch("*/*.txt", "bar.txt", {globstar: true });

assertNotMatch("http://bar.com/*",
               "http://bar.com/bar/baz/jquery.min.js",
               { extended: true,  globstar: true });
assertNotMatch("http://bar.com/*",
               "http://bar.com/bar/baz/jquery.min.js",
               { globstar: true });

assertMatch("http://bar.com/*",
            "http://bar.com/bar/baz/jquery.min.js",
            { globstar: false });
assertMatch("http://bar.com/**",
            "http://bar.com/bar/baz/jquery.min.js",
            { globstar: true });

assertMatch("http://bar.com/*/*/jquery.min.js",
            "http://bar.com/bar/baz/jquery.min.js",
            { globstar: true });
assertMatch("http://bar.com/**/jquery.min.js",
            "http://bar.com/bar/baz/jquery.min.js",
            { globstar: true });
assertMatch("http://bar.com/*/*/jquery.min.js",
            "http://bar.com/bar/baz/jquery.min.js",
            { globstar: false });
assertMatch("http://bar.com/*/jquery.min.js",
            "http://bar.com/bar/baz/jquery.min.js",
            { globstar: false });
assertNotMatch("http://bar.com/*/jquery.min.js",
               "http://bar.com/bar/baz/jquery.min.js",
               { globstar: true });

console.log("Ok!");
