'use strict';
var tape = require('tape');
var path = require('../');

tape('path.posix.dirname', function (t) {
  t.strictEqual(path.posix.dirname('/a/b/'), '/a');
  t.strictEqual(path.posix.dirname('/a/b'), '/a');
  t.strictEqual(path.posix.dirname('/a'), '/');
  t.strictEqual(path.posix.dirname(''), '.');
  t.strictEqual(path.posix.dirname('/'), '/');
  t.strictEqual(path.posix.dirname('////'), '/');
  t.strictEqual(path.posix.dirname('//a'), '//');
  t.strictEqual(path.posix.dirname('bar'), '.');
  t.end();
});

tape('path.win32.dirname', { skip: true }, function (t) {
  t.strictEqual(path.win32.dirname('c:\\'), 'c:\\');
  t.strictEqual(path.win32.dirname('c:\\bar'), 'c:\\');
  t.strictEqual(path.win32.dirname('c:\\bar\\'), 'c:\\');
  t.strictEqual(path.win32.dirname('c:\\bar\\bar'), 'c:\\bar');
  t.strictEqual(path.win32.dirname('c:\\bar\\bar\\'), 'c:\\bar');
  t.strictEqual(path.win32.dirname('c:\\bar\\bar\\baz'), 'c:\\bar\\bar');
  t.strictEqual(path.win32.dirname('\\'), '\\');
  t.strictEqual(path.win32.dirname('\\bar'), '\\');
  t.strictEqual(path.win32.dirname('\\bar\\'), '\\');
  t.strictEqual(path.win32.dirname('\\bar\\bar'), '\\bar');
  t.strictEqual(path.win32.dirname('\\bar\\bar\\'), '\\bar');
  t.strictEqual(path.win32.dirname('\\bar\\bar\\baz'), '\\bar\\bar');
  t.strictEqual(path.win32.dirname('c:'), 'c:');
  t.strictEqual(path.win32.dirname('c:bar'), 'c:');
  t.strictEqual(path.win32.dirname('c:bar\\'), 'c:');
  t.strictEqual(path.win32.dirname('c:bar\\bar'), 'c:bar');
  t.strictEqual(path.win32.dirname('c:bar\\bar\\'), 'c:bar');
  t.strictEqual(path.win32.dirname('c:bar\\bar\\baz'), 'c:bar\\bar');
  t.strictEqual(path.win32.dirname('file:stream'), '.');
  t.strictEqual(path.win32.dirname('dir\\file:stream'), 'dir');
  t.strictEqual(path.win32.dirname('\\\\unc\\share'),
                '\\\\unc\\share');
  t.strictEqual(path.win32.dirname('\\\\unc\\share\\bar'),
                '\\\\unc\\share\\');
  t.strictEqual(path.win32.dirname('\\\\unc\\share\\bar\\'),
                '\\\\unc\\share\\');
  t.strictEqual(path.win32.dirname('\\\\unc\\share\\bar\\bar'),
                '\\\\unc\\share\\bar');
  t.strictEqual(path.win32.dirname('\\\\unc\\share\\bar\\bar\\'),
                '\\\\unc\\share\\bar');
  t.strictEqual(path.win32.dirname('\\\\unc\\share\\bar\\bar\\baz'),
                '\\\\unc\\share\\bar\\bar');
  t.strictEqual(path.win32.dirname('/a/b/'), '/a');
  t.strictEqual(path.win32.dirname('/a/b'), '/a');
  t.strictEqual(path.win32.dirname('/a'), '/');
  t.strictEqual(path.win32.dirname(''), '.');
  t.strictEqual(path.win32.dirname('/'), '/');
  t.strictEqual(path.win32.dirname('////'), '/');
  t.strictEqual(path.win32.dirname('bar'), '.');
  t.end();
});
