var test = require("tape")
var extend = require("./")
var mutableExtend = require("./mutable")

test("merge", function(assert) {
    var a = { a: "bar" }
    var b = { b: "bar" }

    assert.deepEqual(extend(a, b), { a: "bar", b: "bar" })
    assert.end()
})

test("replace", function(assert) {
    var a = { a: "bar" }
    var b = { a: "bar" }

    assert.deepEqual(extend(a, b), { a: "bar" })
    assert.end()
})

test("undefined", function(assert) {
    var a = { a: undefined }
    var b = { b: "bar" }

    assert.deepEqual(extend(a, b), { a: undefined, b: "bar" })
    assert.deepEqual(extend(b, a), { a: undefined, b: "bar" })
    assert.end()
})

test("handle 0", function(assert) {
    var a = { a: "default" }
    var b = { a: 0 }

    assert.deepEqual(extend(a, b), { a: 0 })
    assert.deepEqual(extend(b, a), { a: "default" })
    assert.end()
})

test("is immutable", function (assert) {
    var record = {}

    extend(record, { bar: "bar" })
    assert.equal(record.bar, undefined)
    assert.end()
})

test("null as argument", function (assert) {
    var a = { bar: "bar" }
    var b = null
    var c = void 0

    assert.deepEqual(extend(b, a, c), { bar: "bar" })
    assert.end()
})

test("mutable", function (assert) {
    var a = { bar: "bar" }

    mutableExtend(a, { bar: "baz" })

    assert.equal(a.bar, "baz")
    assert.end()
})

test("null prototype", function(assert) {
    var a = { a: "bar" }
    var b = Object.create(null)
    b.b = "bar";

    assert.deepEqual(extend(a, b), { a: "bar", b: "bar" })
    assert.end()
})

test("null prototype mutable", function (assert) {
    var a = { bar: "bar" }
    var b = Object.create(null)
    b.bar = "baz";

    mutableExtend(a, b)

    assert.equal(a.bar, "baz")
    assert.end()
})

test("prototype pollution", function (assert) {
    var a = {}
    var maliciousPayload = '{"__proto__":{"oops":"It works!"}}'

    assert.strictEqual(a.oops, undefined)
    extend({}, maliciousPayload)
    assert.strictEqual(a.oops, undefined)
    assert.end()
})

test("prototype pollution mutable", function (assert) {
    var a = {}
    var maliciousPayload = '{"__proto__":{"oops":"It works!"}}'

    assert.strictEqual(a.oops, undefined)
    mutableExtend({}, maliciousPayload)
    assert.strictEqual(a.oops, undefined)
    assert.end()
})
